    // text í•„ë“œì—ì„œ JSON ì¶”ì¶œ (LLM ì¶œë ¥)
    let rawText = item.json.text || '';

    // JSON íŒŒì‹±
    let parsedData;
    try {
        // LLM ì‘ë‹µì—ì„œ JSON ë¶€ë¶„ë§Œ ì¶”ì¶œ (ì•ë’¤ ê³µë°± ì œê±°)
        const jsonMatch = rawText.match(/\{[\s\S]*\}/);
        if (jsonMatch) {
            // JSON ë¬¸ìì—´ì„ íŒŒì‹±
            parsedData = JSON.parse(jsonMatch[0]);
        } else {
            // JSONì„ ì°¾ì§€ ëª»í•œ ê²½ìš° ì˜ˆì™¸ ì²˜ë¦¬
            throw new Error('LLM ì¶œë ¥ì—ì„œ JSON êµ¬ì¡°ë¥¼ ì°¾ì„ ìˆ˜ ì—†ìŠµë‹ˆë‹¤.');
        }
    } catch (error) {
        // íŒŒì‹± ì‹¤íŒ¨ ì‹œ ì—ëŸ¬ ê°ì²´ ë°˜í™˜
        return {
            json: {
                error: 'JSON íŒŒì‹± ì‹¤íŒ¨',
                raw: rawText
            }
        };
    }

    // validation_results ì¶”ì¶œ
    const results = parsedData.validation_results || [];

    // 1. ê¸°ì¡´ ë¡œì§: ì›í•˜ëŠ” í˜•ì‹ìœ¼ë¡œ ë³€í™˜ (formatted_validation)
    const formatted = results.map(rule => 
        `{'id': ${rule.id}, 'status': '${rule.status}', 'reason': '${rule.reason}'}`
    );

    // 2. ê¸°ì¡´ ë¡œì§: 'PASS'ì¸ ID ë¦¬ìŠ¤íŠ¸ ì¶”ì¶œ (pass_ids)
    // ì°¸ê³ : ê¸°ì¡´ ì½”ë“œê°€ 'PASS'ê°€ ì•„ë‹Œ í•­ëª©ì„ í•„í„°ë§í•˜ë„ë¡ ì˜ëª»ë˜ì–´ ìˆì–´, 'PASS' í•­ëª©ì„ í•„í„°ë§í•˜ë„ë¡ ìˆ˜ì •í–ˆìŠµë‹ˆë‹¤.
    const passIds = results
        .filter(rule => rule.status === 'PASS') // statusê°€ 'PASS'ì¸ í•­ëª©ë§Œ í•„í„°ë§ (ìˆ˜ì •)
        .map(rule => rule.id);

    // 3. ğŸ”¥ ìƒˆë¡œìš´ ë¡œì§: 'FAIL'ì¸ í•­ëª©ì˜ ì‚¬ìœ (reason) ì¶”ì¶œ
    const failReasons = results
        .filter(rule => rule.status === 'FAIL') // statusê°€ 'FAIL'ì¸ í•­ëª©ë§Œ í•„í„°ë§
        .map(rule => rule.reason); // í•´ë‹¹ í•­ëª©ì˜ reasonë§Œ ì¶”ì¶œí•˜ì—¬ ë°°ì—´ ìƒì„±

    // 4. ğŸ”¥ ìƒˆë¡œìš´ ë¡œì§: 'FAIL' í•­ëª© ì¡´ì¬ ì—¬ë¶€ í™•ì¸ í”Œë˜ê·¸
    const hasFailures = failReasons.length > 0;

    return {
        json: {
            // ê¸°ì¡´ ê²°ê³¼ í•„ë“œ
            formatted_validation: formatted,
            pass_ids: passIds,

            // ğŸ”¥ ìƒˆë¡œìš´ í•„ë“œ: ë¶„ê¸° ì²˜ë¦¬ì— ì‚¬ìš©ë¨
            fail_reasons: failReasons,         // 'FAIL' ì‚¬ìœ  ë¦¬ìŠ¤íŠ¸
            has_failures: hasFailures,          // 'FAIL' ì¡´ì¬ ì—¬ë¶€ (true/false)

            // LLMì˜ ìµœì¢… íŒì • ê²°ê³¼ë¥¼ í•¨ê»˜ ì „ë‹¬ (IF ë¶„ê¸°ì— ìœ ìš©)
            overall_decision: parsedData.overall_decision || 'ì¶”ê°€ê²€í† í•„ìš”'
        }
    };
});

