for (const item of $input.all()) {
    // ----------------------------------------------------------------------
    // í•µì‹¬ ìˆ˜ì •: ë“±ê¸°ë¶€ë“±ë³¸ í…ìŠ¤íŠ¸ ì†ŒìŠ¤ ë³€ê²½
    // Merge ë…¸ë“œë¥¼ ê±°ì¹œ í›„ ë“±ê¸°ë¶€ë“±ë³¸ OCR í…ìŠ¤íŠ¸ê°€ 'data' í•„ë“œì— ë“¤ì–´ì˜¤ê³  ìˆìŠµë‹ˆë‹¤.
    // 'data' í•„ë“œë¥¼ ìµœìš°ì„ ìœ¼ë¡œ ì‚¬ìš©í•©ë‹ˆë‹¤.
    // ----------------------------------------------------------------------
    const text = item.json.data || item.json.registry_text || item.json.extractedText || ''; 

    // =========================================================
    // 1. ê³µê²©ì ì¸ í…ìŠ¤íŠ¸ ì „ì²˜ë¦¬
    // =========================================================
    const cleanText = text
        .replace(/\n/g, ' ') // ì¤„ë°”ê¿ˆì„ ê³µë°±ìœ¼ë¡œ í†µì¼
        // í•œê¸€ê³¼ ê³µë°±ì„ ì œì™¸í•œ ëª¨ë“  ë¬¸ì(ìˆ«ì, ê¸°í˜¸)ë¥¼ ê³µë°±ìœ¼ë¡œ ëŒ€ì²´í•©ë‹ˆë‹¤.
        // ì´ë¥¼ í†µí•´ '1568-1ì™¸ 1í•„ì§€'ì™€ ê°™ì€ ì£¼ì†Œ ë°©í•´ ìš”ì†Œê°€ ì œê±°ë©ë‹ˆë‹¤.
        .replace(/[^\uAC00-\uD7A3\s\d-]/g, ' ') 
        // 2ê°œ ì´ìƒì˜ ì—°ì†ëœ ê³µë°±ì„ í•˜ë‚˜ì˜ ê³µë°±ìœ¼ë¡œ ì¶•ì†Œ
        .replace(/\s{2,}/g, ' '); 

    // 2. ì •ê·œì‹ ì •ì˜ (ìœ ì—°í•œ ì •ê·œì‹ êµ¬ì¡°)
    // ì‹œ/ë„(ê·¸ë£¹1) + (ì„ íƒì  ì‹œ/êµ°) + êµ¬(ê·¸ë£¹2) + ë™/ë¡œ/ê¸¸/ì/ë©´/ê°€(ê·¸ë£¹3)
    const revisedAddressRegex = /([ê°€-í£]+(?:ì‹œ|ë„))\s+(?:[ê°€-í£]+(?:ì‹œ|êµ°)\s+)?([ê°€-í£]+êµ¬)\s+([ê°€-í£]+(?:ë™|ë¡œ|ê¸¸|ì|ë©´|ê°€))/;

    const match = cleanText.match(revisedAddressRegex);

    let extractedAddress = 'ì£¼ì†Œ ë¯¸í™•ì¸';

    if (match) {
        // match[1]: ì‹œ/ë„ (ì˜ˆ: ì„œìš¸íŠ¹ë³„ì‹œ)
        // match[2]: êµ¬ (ì˜ˆ: ê´€ì•…êµ¬)
        // match[3]: ë™/ë¡œ/ê¸¸ (ì˜ˆ: ë´‰ì²œë™)
        extractedAddress = `${match[1]} ${match[2]} ${match[3]}`;
    } else {
        // [ë°±ì—…] ì‹œ/ë„ ì •ë³´ê°€ ì—†ëŠ” ê²½ìš° (ì˜ˆ: "ë¶„ë‹¹êµ¬ ì •ìë™")
        const backupRegex = /([ê°€-í£]+êµ¬)\s+([ê°€-í£]+(?:ë™|ë¡œ|ê¸¸|ì|ë©´|ê°€))/;
        const backupMatch = cleanText.match(backupRegex);
        if(backupMatch) {
            extractedAddress = `${backupMatch[1]} ${backupMatch[2]}`;
        }
    }

    // ê²°ê³¼ ì €ì¥
    item.json.address_full = extractedAddress;
}

return $input.all();

